'use strict';

const StepAction = require('./stepAction');
require('bluebird');

class RefreshStepAction extends StepAction {
    execute() {
        return this.driver.reloadTab()
            .return({ success: true })
            .catch(error => {
                return { success: false, reason: error.message };
            });
    }
}

module.exports = RefreshStepAction;
